import React from 'react'
import './Detail.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import Mouse from './assets/mouse.png';
import { useParams } from 'react-router-dom';
import Navigation from '../Navigation/Navigation';

// export default function Detail(props) {

class Detail extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            image: 'https://unsplash.com/photos/4PchFKrUw84',
            name: 'Mouse',
            stock: 600
        }
    }

    // const params = useParams()
    // console.log(params);

    render() {
        return (
            <div>
                <Navigation />

                {/* CARD */}
                <div style={{ marginTop: "120px" }}>
                    <div className='card mb-3' style={{ backgroundColor: "black" }}>
                        <div className='row g-0'>
                            <div className='col-md-6'>
                                <img src={this.state.image} className='img-fluid rounded-start' alt="" />
                            </div>

                            <div className='col-md-6' style={{ margin: "auto" }}>
                                <div className='card-body-product'>
                                    <h5 className='card-title fw-bold' style={{ fontSize: "30px", color: "red" }}>{this.state.name}</h5>
                                    <p className='card-text'><small className='text-muted'>{this.state.stock}</small></p>
                                    <p className='card-text'>
                                        - Wireless Mouse <br />
                                        - Konektivitas wireless 2.4 GHz <br />
                                        - Jarak wireless hingga 10 m <br />
                                        - Plug and Play <br />
                                        - Baterai tahan hingga 12 bulan <br />
                                    </p>

                                    <p className='card-text' style={{ textAlign: "justify" }}>
                                        Mouse Wireless Alytech AL - Y5D, hadir dengan desain 3 tombol mouse yang ringan dan
                                        mudah dibawa. Mouse ini menggunakan frekuensi radio 2.4GHz (bekerja hingga jarak 10m)
                                        dan fitur sensor canggih optik pelacakan dengan penerima USB yang kecil. Mouse ini
                                        didukung oleh 1x baterai AA (hingga 12 bulan hidup baterai). mendukung sistem operasi
                                        Windows 7,8, 10 keatas, Mac OS X 10.8 atau yang lebih baru dan sistem operasi Chrome OS.
                                    </p>

                                    <p className='card-text fw-bold' style={{ textAlign: "right", fontSize: "17px", color: "red" }}>
                                        Rp.300.900
                                    </p>

                                    <button type='button' className='btn btn-danger'>
                                        <Link to='/profile' style={{ textDecoration: "none", color: "white" }}>
                                            Buy
                                        </Link>
                                    </button>
                                </div>
                            </div>

                        </div>
                    </div>
                </div>
                {/* CARD */}
            </div>
        )
    }
}

export default Detail
