import React, { Component } from 'react'
import './ListCategory.css';
import { Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import DeleteButton from '../../../components/DeleteButton/DeleteButton';
import NavigationAdmin from '../NavigationAdmin/NavigationAdmin';



const ListCategory = () => {
    return (
        <div>
            <div>
                {/* NAVBAR */}
                <Navbar collapseOnSelect expand="lg" variant="dark" className='navbar' fixed='top'>
                    <Container>
                        <Navbar.Brand href="#home">
                            <Link to="/">
                                <img src={Frame} alt="Logo DumbMerch"
                                    style={{ width: "60px" }}
                                />
                            </Link>
                        </Navbar.Brand>
                        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                        <Navbar.Collapse id="responsive-navbar-nav">
                            <Nav className="me-auto">
                            </Nav>
                            <Nav>
                                <Nav.Link>
                                    <Link to="/user-complain" style={{ textDecoration: "none", color: "white" }}>Complain</Link>
                                </Nav.Link>
                                {/* <Nav.Link>
                                    <Link to="/profile" style={{ textDecoration: "none", color: "white" }}>Profile</Link>
                                </Nav.Link> */}
                                <Nav.Link>
                                    <Link to="/list-category" style={{ textDecoration: "none", color: "orangered" }}>Category</Link>
                                </Nav.Link>
                                <Nav.Link>
                                    <Link to="/list-product" style={{ textDecoration: "none", color: "white" }}>Product</Link>
                                </Nav.Link>
                                <Nav.Link>
                                    <Link to="/login" style={{ textDecoration: "none", color: "white" }}>Logout</Link>
                                </Nav.Link>
                                <Nav.Link href="#deets">
                                    <Link to="/cart" className='cart' style={{ textDecoration: "none", color: "white" }}>
                                        <i class="fa-solid fa-cart-shopping"></i>
                                    </Link>
                                </Nav.Link>
                            </Nav>
                        </Navbar.Collapse>
                    </Container>
                </Navbar>
                {/* NAVBAR */}
            </div>

            <div className='container'>
                <div>
                    <h3 className='list-category'>List Category</h3>
                </div>

                <Table striped hover variant="dark">
                    <thead>
                        <tr>
                            <th style={{ width: "30%" }}>No</th>
                            <th style={{ width: "30%" }}>Category Name</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>1</td>
                            <td>Mouse</td>
                            <td>
                                <span className='button'>
                                    <button className='btn btn-sm btn-success'>
                                        <Link to='/edit-category' style={{ textDecoration: "none", color: "white" }}>
                                            Edit
                                        </Link>
                                    </button>
                                    <button className='btn btn-sm  btn-success'
                                        style={{
                                            backgroundColor: "red",
                                            textDecoration: "none",
                                            border: "none",
                                            padding: "6px 43px"
                                        }}>Delete

                                    </button>
                                </span>
                            </td>
                        </tr>
                        <tr>
                            <td>2</td>
                            <td>Keyboard</td>
                            <td>
                                <span className='button'>
                                    <button className='btn btn-sm btn-success'>Edit</button>
                                    <button className='btn btn-sm  btn-success'
                                        style={{
                                            backgroundColor: "red",
                                            textDecoration: "none",
                                            border: "none",
                                            padding: "6px 43px"
                                        }}>Delete
                                    </button>
                                </span>
                            </td>
                        </tr>
                        <tr>
                            <td>3</td>
                            <td>Bag</td>
                            <td>
                                <span className='button'>
                                    <button className='btn btn-sm btn-success'>Edit</button>
                                    <button className='btn btn-sm  btn-success'
                                        style={{
                                            backgroundColor: "red",
                                            textDecoration: "none",
                                            border: "none",
                                            padding: "6px 43px"
                                        }}>Delete
                                    </button>
                                </span>
                            </td>
                        </tr>
                        <tr>
                            <td>4</td>
                            <td>Stationary</td>
                            <td>
                                <span className='button'>
                                    <button className='btn btn-sm btn-success'>Edit</button>
                                    <button className='btn btn-sm  btn-success'
                                        style={{
                                            backgroundColor: "red",
                                            textDecoration: "none",
                                            border: "none",
                                            padding: "6px 43px"
                                        }}>Delete
                                    </button>
                                </span>
                            </td>
                        </tr>
                        <tr>
                            <td>5</td>
                            <td>Doll</td>
                            <td>
                                <span className='button'>
                                    <button className='btn btn-sm btn-success'>Edit</button>
                                    <button className='btn btn-sm  btn-success'
                                        style={{
                                            backgroundColor: "red",
                                            textDecoration: "none",
                                            border: "none",
                                            padding: "6px 43px"
                                        }}>Delete
                                    </button>
                                </span>
                            </td>
                        </tr>
                        <tr>
                            <td>6</td>
                            <td>Pillow</td>
                            <td>
                                <span className='button'>
                                    <button className='btn btn-sm btn-success'>Edit</button>
                                    <button className='btn btn-sm  btn-success'
                                        style={{
                                            backgroundColor: "red",
