import React, { useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';
import Frame from './assets/Frame.svg';
import cssModules from './Login.module.css';
import { Link } from 'react-router-dom';
// import loginStyle from '../styles/LoginStyle.css';
// import React, { useState } from 'react';


function Login() {
    const [state, setState] = useState({
        isLogin: false,
        user: {
            email: '',
            password: '',
        },
    });

    useEffect(() => {
        console.log('App Component Did Mount');
        console.log(state);
    }, []);

    useEffect(() => {
        if (state.isLogin == true) {
            console.log('App Component Did Update');
            console.log(state);
        }
    }, [state]);

    const handleOnSubmit = (e) => {
        e.preventDefault();
        const email = document.getElementById('email').values;
        const password = document.getElementById('password').values;
        setState({
            isLogin: true,
            user: {
                email,
                password,
            },
        });
    };

    return (

        <div style={{ marginTop: "150px" }}>
            <div className='container' style={{ width: "70%" }}>
                <div className={cssModules.card}>
                    <div className='row g-0'>
                        {/* FRAME */}
                        <div className='col-md-8'>
                            <div style={{ textAlign: "left" }}>
                                <div className={cssModules.box}>
                                    <div >
                                        <img src={Frame} alt=""
                                            style={{ width: "250px" }}
                                        />
                                    </div>
                                    <div>
                                        <h1 className={cssModules.cardTitle}>Easy, Fast and Reliable</h1>
                                        <p className="card-text" style={{ marginTop: "10px" }}>Go shopping for merchandise, just go to dumb merch shopping. the biggest merchandise in <b>Indonesia.</b></p>
                                        {/* <p style={{ marginTop: "-17px" }}>the biggest merchandise in <b>Indonesia.</b></p> */}
                                    </div>
                                    <div className='button' style={{ marginTop: "50px" }}>
                                        <span style={{ marginRight: "10px" }}>
                                            <Button variant="danger" type='btn btn-sm'
                                                style={{ width: "120px" }}>
                                                <Link to="/login" style={{ textDecoration: "none", color: "white" }}>Login</Link></Button>
                                        </span>
                                        <span>
                                            <Button variant="none" style={{ color: "white" }}>
                                                <Link to="/register" style={{ textDecoration: "none", color: "white" }}>Register</Link></Button>
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </div>
                        {/* FRAME */}


                        {/* FORM */}
                        <div className='col-md-4'>
                            <Form onSubmit={handleOnSubmit} style={{
                                width: "100%",
                                backgroundColor: "rgb(36, 36, 36)",
                                padding: "20px",
                                margin: "auto",
                                marginTop: "35px",
                                marginBottom: "30px",
                                borderRadius: "5px"
                            }}>
                                <h2 className="text-start">Login</h2>
                                <Form.Group className="mb-3" controlId="formBasicEmail">
                                    <Form.Control type="email" placeholder="Email" className="formInput" style={{ backgroundColor: "rgb(48, 47, 47)", color: "white" }} />
                                </Form.Group>
                                <Form.Group className="mb-3" controlId="formBasicPassword">
                                    <Form.Control type="password" placeholder="Password" className="formInput" style={{ backgroundColor: "rgb(48, 47, 47)", color: "white" }} />
                                </Form.Group>

                                <div className='d-grid gap-2'>
                                    <Button variant="danger" type="button">
                                        <Link to='/' style={{ textDecoration: "none", color: "white" }}>Login</Link>
                                    </Button>
                                </div>
                            </Form>
                        </div>
                        {/* FORM */}
                    </div>
                </div>
            </div>
        </div >
    )
}

export default Login
