import React from 'react'
import './CardProduct.css'
import { Card, ListGroup, ListGroupItem } from 'react-bootstrap';
import { Link } from 'react-router-dom';

// import Mouse from './assets/mouse.png';
// import Keyboard from './assets/keyboard.png';
import DummyProduct from '../../back/Data/DummyProduct/DummyProduct';


// const styles = {
//     cardContainer: {
//         width: '241px',
//         height: '410px',
//         backgroundColor: '#212121',
//         marginRight: '20px',
//         marginTop: '120px',
//     },

//     img: {
//         width: '241px',
//         height: '312px',
//         objectFit: 'cover',
//     },

//     title: {
//         color: '#F74D4D',
//         fontSize: '18px',
//         paddingLeft: '15px',
//         margin: '7px 0px',
//     },

//     p: {
//         color: 'white',
//         fontSize: '14px',
//         paddingLeft: '15px',
//         marginBottom: '5px',
//     },

//     textProduct: {
//         color: 'red',
//         marginTop: '320px',
//         fontSize: '60px',
//     },
// }

function CardProduct(product) {
    return (


        <div className='container-card'>
            <div className='d-flex flex-wrap'>
                <div className='card-header'>
                    <div className='row g-0'>
                        <div className='text-product'>
                            <h3>Product</h3>
                        </div>
                        {/* <div style={{ margin: 'auto', display: 'flex' }}> */}

                        {
                            DummyProduct.map((product) => (

                                <div className='col-md-4 my-3'>

                                    <Card style={{ width: '20rem', marginRight: "50px", marginTop: "20px", margin: "auto" }}>
                                        <Card.Img variant="top" src={product.img} className='mouse' />
                                        <ListGroup className="list-group-flush">
                                            <ListGroupItem className='text fw-bold'>
                                                <Link to='/product/:1' style={{ textDecoration: "none", color: "red" }}>
                                                    {product.title}
                                                </Link>
                                            </ListGroupItem>
                                            <ListGroupItem className='text2'>{product.price}</ListGroupItem>
                                            <ListGroupItem className='text2'>{product.stock}
                                                <button className='btn btn-danger' type='button'>
                                                    <Link to='/cart' style={{ textDecoration: "none", color: "white" }}>
                                                        Add to Cart
                                                    </Link>
                                                </button>
                                            </ListGroupItem>
                                        </ListGroup>
                                    </Card>
                                </div>
                                // <div style={styles.cardContainer}>
                                //     <img style={styles.img} src={product.img} alt='' />
                                //     <Link to="/detail-product"><h6 style={styles.title}>{product.title}</h6></Link>
                                //     <p style={styles.p}>Rp.{product.price}</p>
                                //     <p style={styles.p}>Stock : {product.stock}</p>
                                // </div>
                            ))
                        }

                    </div>
                </div>
            </div>
        </div>

        //         <Link
        //             to={`/product/` + item.id}
        //             style={{ textDecoration: "none" }}
        //             key={index}
        //         >
        //             <div className="card-product mt-3">
        //                 <img src={item.image} className="img-fluid img-rounded" />
        //                 <div className="p-2">
        //                     <div className="text-header-product-item">{item.name}</div>
        //                     <div className="text-product-item">
        //                         (item.price)
        //                     </div>
        //                     <div className="text-product-item">Stock : {item.qty}</div>
        //                 </div>
        //             </div>
        //         </Link>
    )
}

export default CardProduct;
