import React from 'react';
import { Form, Button } from 'react-bootstrap';
import Frame from './assets/Frame.svg';

function Login() {
    return (

        <div style={{ marginTop: "150px" }}>
            <div className="card" style={{ width: "100%", height: "auto" }}>
                {/* <div className='container'> */}
                <div className='row g-0'>
                    {/* <div className='justify-content-center'> */}
                    {/* <div className='card-body'> */}
                    <div className='col-md-6'>
                        {/* <div className='box' style={{ margin: "10px", width: "500px", backgroundColor: "red" }}> */}
                        <div style={{ justifyContent: "flex-start" }}>
                            <img src={Frame} alt="" className='justify-content-start'
                                style={{ width: "250px" }}
                            />
                        </div>
                        <h1 className="fw-bold" style={{ marginTop: "10px" }}>Easy, Fast and Reliable</h1>
                        <p style={{ marginTop: "10px" }}>Go shopping for merchandise, just go to dumb merch</p>
                        <p style={{ marginTop: "-17px" }}>shopping. the biggest merchandise in <b>Indonesia.</b></p>

                        <div className='button' style={{ marginTop: "50px" }}>
                            <span style={{ marginRight: "10px" }}>
                                <Button variant="danger" type='btn btn-sm'
                                    style={{ width: "120px" }}>Login</Button>
                            </span>
                            <span>
                                <Button variant="none" style={{ color: "white" }}>Register</Button>
                            </span>
                        </div>
                    </div>
                    {/* </div> */}
                    {/* </div> */}

                    {/* <div className='justify-content-end'> */}
                    <div className='col-md-6'>

                        <Form style={{
                            width: "60%",
                            backgroundColor: "rgb(36, 36, 36)",
                            padding: "50px",
                            margin: "auto",
                            marginTop: "35px",
                            borderRadius: "8px"
                        }}>
                            <h2 className="text-start" style={{ marginBottom: "15px", color: "white" }}>Login</h2>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Control type="email" placeholder="Email" className='input' />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Control type="password" placeholder="Password" />
                            </Form.Group>
                            <div className='d-grid gap-2'>
                                <Button variant="danger" type="submit">
                                    Login
                                </Button>
                            </div>
                        </Form>

                    </div>
                    {/* </div> */}
                    {/* </div> */}
                </div>
                {/* </div> */}
            </div>
        </div >
    )

}

export default Login
