import React from 'react'
import './Product.css';
import { Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import DeleteButton from '../../../components/DeleteButton/DeleteButton';
import NavigationAdmin from '../NavigationAdmin/NavigationAdmin';


// class ListProduct extends Component {
// render() {

//     console.log(this.props)

const Product = () => {

    const categories = [
        { no: '1', photo: 'Mouse.jpg', productName: 'Mouse', productDesc: 'Lorem Ipsum Mouse...', price: '300.000', stock: '600' },
        { no: '2', photo: 'Keyboard.jpg', productName: 'Keyboard', productDesc: 'Lorem Ipsum Keyboard...', price: '700.000', stock: '600' },
        { no: '3', photo: 'Bag.jpg', productName: 'Bag', productDesc: 'Lorem Ipsum Bag...', price: '300.000', stock: '600' },
        { no: '4', photo: 'Stationary.jpg', productName: 'Stationary', productDesc: 'Lorem Ipsum Stationary...', price: '25.000', stock: '600' },
        { no: '5', photo: 'Doll.jpg', productName: 'Doll', productDesc: 'Lorem Ipsum Doll...', price: '125.000', stock: '600' },
        { no: '6', photo: 'Pillow.jpg', productName: 'Pillowe', productDesc: 'Lorem Ipsum Pillow...', price: '300.000', stock: '600' },
    ]


    const ListProduct = (product, index) => {

        return (
            <tr key={index}>
                <td>{product.no}</td>
                <td>{product.photo}</td>
                <td>{product.productName}</td>
                <td>{product.productDesc}</td>
                <td>{product.price}</td>
                <td>{product.stock}</td>
                <td>
                    <span className='button'>
                        <button className='btn btn-sm btn-success'>
                            <Link to='/edit-product' style={{ textDecoration: "none", color: "white" }}>
                                Edit
                            </Link>
                        </button>
                        <DeleteButton />
                    </span>
                </td>
            </tr>
        );
    };

    return (
        <div>
            <NavigationAdmin />

            <div className='container'>
                <div>
                    <h3 className='list-category'>List Product</h3>
                </div>

                <Table striped hover variant="dark">
                    <thead>
                        <tr>
                            <th>No</th>
                            <th style={{ width: "15%" }}>Photo</th>
                            <th style={{ width: "15%" }}>Product Name</th>
                            <th style={{ width: "18%" }}>Product Desc</th>
                            <th style={{ width: "8%" }}>Price</th>
                            <th style={{ width: "8%" }}>Qty</th>
                            <th style={{ width: "30%" }}>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {categories.map(ListProduct)}
                    </tbody>
                </Table>
            </div>
        </div>
    )
};

export default ListProduct
