import React from 'react'
import { Card, ListGroup, ListGroupItem } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Mouse from './assets/mouse.png';
import Keyboard from './assets/keyboard.png';
import DummyProduct from '../../back/Data/DummyProduct/DummyProduct';


const styles = {
    cardContainer: {
        width: '241px',
        height: '410px',
        backgroundColor: '#212121',
        marginRight: '20px',
        marginTop: '120px',
    },

    img: {
        width: '241px',
        height: '312px',
        objectFit: 'cover',
    },

    title: {
        color: '#F74D4D',
        fontSize: '18px',
        paddingLeft: '15px',
        margin: '7px 0px',
    },

    p: {
        color: 'white',
        fontSize: '14px',
        paddingLeft: '15px',
        marginBottom: '5px',
    },

    textProduct: {
        color: 'red',
        marginTop: '320px',
        fontSize: '60px',
    },
}

function CardProduct() {
    return (
        // <div style={{ display: 'flex', }}>
        //     {
        //         DummyProduct.map((product) => (
        //             <div>
        //                 <img ssrc={product.img} alt='' />
        //                 <Link to="/detail-product"><h6>{product.title}</h6></Link>
        //                 <p>Rp.{product.price}</p>
        //                 <p>Stock : {product.stock}</p>
        //             </div>
        //         ))
        //     }
        // </div>

        <div>
            <div className='card-header'>
                <div className='row g-0'>
                    <div className='text-product'>
                        <h3>Product</h3>
                    </div>
                    {/* CARD 1 */}
                    <div className='col'>
                        <Card style={{ width: '15rem', marginRight: "50px", marginTop: "20px", margin: "auto" }}>
                            <Card.Img variant="top" src={Mouse} className='mouse' />
                            <ListGroup className="list-group-flush">
                                <ListGroupItem className='text fw-bold'>
                                    <Link to='/product/:1' style={{ textDecoration: "none", color: "red" }}>
                                        Mouse
                                    </Link>
                                </ListGroupItem>
                                <ListGroupItem className='text2'>Harga: Rp.500.000</ListGroupItem>
                                <ListGroupItem className='text2'>Stock: 600
                                    <button className='btn btn-danger' type='button'>
                                        <Link to='/cart' style={{ textDecoration: "none", color: "white" }}>
                                            Add to Cart
                                        </Link>
                                    </button>
                                </ListGroupItem>
                            </ListGroup>

                        </Card>
                    </div>
                    {/* CARD 1 */}
                </div>
            </div>



            <div style={{ display: 'flex', }}>
                {
                    DummyProduct.map((product) => (
                        <div style={styles.cardContainer}>
                            <img style={styles.img} src={product.img} alt='' />
                            <Link to="/detail-product"><h6 style={styles.title}>{product.title}</h6></Link>
                            <p style={styles.p}>Rp.{product.price}</p>
                            <p style={styles.p}>Stock : {product.stock}</p>
                        </div>
                    ))
                }
            </div>
        </div>

    )
}

export default CardProduct;
